import React, { useState, } from "react";
import { useNavigate } from 'react-router-dom';
import axios from "axios";

import './Add.css';

const Add = () => {
  const navigate = useNavigate();
  const [userData, setUserData] = useState([]);

  const [formData, setFormData] = useState({
    firstName: "",
    lastName: "",
    emailId: "",
  });

  useEffect(() => {
    axios
      .get("http://localhost:5001/users")
      .then((res) => {
        setUserData(res.data);
      })
      .catch((error) => {
        console.log(error);
      });
  }, []);

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    axios
      .post("http://localhost:5001/users", formData)
      .then((res) => {
        setUserData([...userData, res.data]);
        setFormData({
          firstName: "",
          lastName: "",
          emailId: "",
        });
      })
      .catch((error) => {
        console.log(error);
      });
  };





  return (
    <div className="container">
      <div className="card">
        <h2 className="card-title">Add New User</h2>
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label htmlFor="first-name">First Name</label>
            <input type="text" id="first-name" name="first-name" onChange={handleInputChange}/>
          </div>
          <div className="form-group">
            <label htmlFor="last-name">Last Name</label>
            <input type="text" id="last-name" name="last-name" onChange={handleInputChange} />
          </div>
          <div className="form-group">
            <label htmlFor="email">Email</label>
            <input type="email" id="email" name="email"  onChange={handleInputChange} />
          </div>
          <button type="submit" className="add-button" >
            add
          </button>
          <button type="submit" className="back-button" onClick={()=> navigate("/") } >
            Go back
          </button>
        </form>
      </div>
    </div>
  );
};

export default Add;
