import React, { useState } from "react";
import { useNavigate } from 'react-router-dom';
import axios from "axios";

import './Add.css';

const Add = () => {
  const navigate = useNavigate();

  const [firstName, setFirstName] = useState("");
  const [lastName, setLastName] = useState("");
  const [email, setEmail] = useState("");

  const handleSubmit = (event) => {
    event.preventDefault();

    const newUser = {
      firstName: firstName,
      lastName: lastName,
      email: email
    };

    axios.post("http://localhost:5001/users", newUser)
      .then((res) => {
        console.log(res.data);
        navigate("/");
      })
      .catch((error) => {
        console.log(error);
      });
  };





  return (
    <div className="container">
      <div className="card">
        <h2 className="card-title">Add New User</h2>
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label htmlFor="first-name">First Name</label>
            <input type="text" id="first-name" name="first-name" onChange={(e) => setFirstName(e.target.value)}/>
          </div>
          <div className="form-group">
            <label htmlFor="last-name">Last Name</label>
            <input type="text" id="last-name" name="last-name" onChange={(e) => setLastName(e.target.value)} />
          </div>
          <div className="form-group">
            <label htmlFor="email">Email</label>
            <input type="email" id="email" name="email"  onChange={(e) => setEmail(e.target.value)} />
          </div>
          <button type="submit" className="add-button" >
            add
          </button>
          <button type="submit" className="back-button" onClick={()=> navigate("/") } >
            Go back
          </button>
        </form>
      </div>
    </div>
  );
};

export default Add;
