import React, { useState, useEffect } from "react";
import {useNavigate} from 'react-router-dom'
import axios from "axios";

import "./Table.css";

const Table = () => {
  const navigate = useNavigate();
  const [currentPage, setCurrentPage] = useState(1);
  const [itemsPerPage, setItemsPerPage] = useState(10);


  const [userData, setUserData] = useState([]);

  useEffect(() => {
    axios
      .get("http://localhost:5001/users")
      .then((res) => {
        setUserData(res.data);
      })
      .catch((error) => {
        console.log(error);
      });
  }, []);



// delete data in table 

  const handleDelete = (id) => {
    axios
      .delete(`http://localhost:5001/users/${id}`)
      .then((res) => {
        setUserData(userData.filter((user) => user._id !== id));
      })
      .catch((error) => {
        console.log(error);
      });
  };

  // pagination 
  const totalPages = Math.ceil(userData.length / itemsPerPage);






  return (
    <div className="container">
      <h1 className="title">Python Flask And React Crud Task</h1>
      <button className="add-user-button" onClick={()=> navigate("/add")}>Add User</button>
      <table>
        <thead>
          <tr>
            <th>ID</th>
            <th>First Name</th>
            <th>Last Name</th>
            <th>Email</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {userData.map((user) => (
            <tr key={user._id}>
              <td>{user._id}</td>
              <td>{user.firstName}</td>
              <td>{user.lastName}</td>
              <td>{user.emailId}</td>
              <td>
                <button className="edit">Edit</button>{" "}
                <button className="delete" onClick={() => handleDelete(user._id)}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Table;
