import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";

import "./Table.css";

const Table = () => {
  const navigate = useNavigate();

  const [page, setPage] = useState(1);
  const [pageSize, setPageSize] = useState(5);
  const [userData, setUserData] = useState({ users: [], totalPages: 1 });

  useEffect(() => {
    axios
      .get(`http://localhost:5001/users?page=${page}&size=${pageSize}`)
      .then((res) => {
        setUserData({ users: res.data.users, totalPages: res.data.totalPages });
      })
      .catch((error) => {
        console.log(error);
      });
  }, [page, pageSize]);

  const handleDelete = (id) => {
    axios
      .delete(`http://localhost:5001/users/${id}?page=${page}&size=${pageSize}`)
      .then((res) => {
        setUserData({
          ...userData,
          users: userData.users.filter((user) => user._id !== id),
        });
      })
      .catch((error) => {
        console.log(error);
      });
  };

  const handlePrevious = () => {
    setPage(page - 1);
  };

  const handleNext = () => {
    setPage(page + 1);
  };

  return (
    <div className="container">
      <h1 className="title">Python Flask And React Crud Task</h1>
      <button className="add-user-button" onClick={() => navigate("/add")}>
        Add User
      </button>
      <table>
        <thead>
          <tr>
            <th>ID</th>
            <th>First Name</th>
            <th>Last Name</th>
            <th>Email</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {userData.users.map((user) => (
            <tr key={user._id}>
              <td>{user._id}</td>
              <td>{user.firstName}</td>
              <td>{user.lastName}</td>
              <td>{user.emailId}</td>
              <td>
                <button className="edit">Edit</button>{" "}
                <button
                  className="delete"
                  onClick={() => handleDelete(user._id)}
                >
                  Delete
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
      <div className="pagination">
        <button className="pre" onClick={handlePrevious} disabled={page === 1}>&#x2770;&nbsp;Pre</button>
        <span className="count">Page {page} of {userData.totalPages}</span>
        <button className="next">Next &nbsp;&#x2771;</button>
      </div>
    </div>
  );
};

export default Table;
